.StoryBodyCompanionColumn img[title="Sebastian Modak"] {
	/* display: none; */
	opacity: 0;
	margin-bottom: -40px;
}
html.Hybrid article.article p[itemprop="author creator"],
.StoryBodyCompanionColumn p[itemprop="author creator"] {
	margin: 0 auto 0 auto;
	position: absolute;
	left: 50%;
	transform: translate(-50%, 0);
	font: 700 12px/16px nyt-cheltenham-small;
}
