.StoryBodyCompanionColumn p[itemprop="author creator"] span[itemprop="name"] {
	text-transform: uppercase;
	letter-spacing: 0.05em;
}
html.Hybrid article.article time.TimestampBlock-timestampBlock,
.StoryBodyCompanionColumn time {
	text-align: center;
	font: 400 12px/15px nyt-cheltenham-small;
	text-transform: uppercase;
	margin-top: -1px;
}
html.Hybrid article.article ul,
.StoryBodyCompanionColumn ul {
	border: 1px #D0D5D6 solid;
	border-radius: 4px;
	padding: 22px 20px 0 22px;
	width: auto;
	margin-top: -17px;
	margin-bottom: 32px;
}
html.Hybrid article.article ul li,
.StoryBodyCompanionColumn ul li {
	border-bottom: 1px #D0D5D6 solid;
	padding-bottom: 3px;
	margin-bottom: 20px;
}
html.Hybrid article.article ul li:last-of-type,
.StoryBodyCompanionColumn ul li:last-of-type {
	margin-bottom: 4px;
	border: none;
}

